import React, { useEffect, useState } from "react";
import useForm from "../../hook/form";
import { FormGroup, Card, Elevation, Button } from "@blueprintjs/core";


import { v4 as uuid } from "uuid";

const ToDo = () => {
  const [list, setList] = useState([]);
  const [incomplete, setIncomplete] = useState([]);
  const { handleChange, handleSubmit } = useForm(addItem);

  function addItem(item) {
    console.log(item);
    item.id = uuid();
    item.complete = false;
    setList([...list, item]);
  }

  function deleteItem(id) {
    const items = list.filter((item) => item.id !== id);
    setList(items);
  }

  function toggleComplete(id) {
    const items = list.map((item) => {
      if (item.id == id) {
        item.complete = !item.complete;
      }
      return item;
    });

    setList(items);
  }

  useEffect(() => {
    let incompleteCount = list.filter((item) => !item.complete).length;
    setIncomplete(incompleteCount);
    document.title = `To Do List: ${incomplete}`;
  }, [list]);

  return (
    <>
      <header style={{ width: "1000px", margin: "0 auto" }}>
        <nav
          className="bp3-navbar .modifier "
          style={{ color: "white", backgroundColor: "#040303" }}
        >
          <h1>To Do List: ({incomplete})</h1>
        </nav>
      </header>

      {/* <form onSubmit={handleSubmit}>
        <h2>Add To Do Item</h2>

        <label>
          <span>To Do Item</span>
          <input
            onChange={handleChange}
            name="text"
            type="text"
            placeholder="Item Details"
          />
        </label>

        <label>
          <span>Assigned To</span>
          <input
            onChange={handleChange}
            name="assignee"
            type="text"
            placeholder="Assignee Name"
          />
        </label>

        <label>
          <span>Difficulty</span>
          <input
            onChange={handleChange}
            defaultValue={3}
            type="range"
            min={1}
            max={5}
            name="difficulty"
          />
        </label>

        <label>
          <button type="submit">Add Item</button>
        </label>
      </form> */}

      <div className="div-flex">
        <div className="toDo">
          <Card interactive={true} elevation={Elevation.TWO}>
            <form onSubmit={handleSubmit}>
              <h2>Add To Do Item</h2>
              <FormGroup label="To Do Item" labelFor="text-input">
                <input
                  class="bp3-input .modifier"
                  onChange={handleChange}
                  name="text"
                  type="text"
                  placeholder="Item Details"
                  dir="auto"
                />
              </FormGroup>

              <FormGroup label="Assigned To" labelFor="assignee">
                <input
                  class="bp3-input .modifier"
                  onChange={handleChange}
                  name="assignee"
                  type="text"
                  placeholder="Assignee Name"
                  dir="auto"
                />
              </FormGroup>
              <br />
              <FormGroup label="Difficulty" labelFor="assignee">
                <input
                  onChange={handleChange}
                  defaultValue={3}
                  type="range"
                  min={1}
                  max={5}
                  name="difficulty"
                  dir="auto"
                />
              </FormGroup>

              <br />
              <label>
                <Button type="submit">Add Item</Button>
              </label>
            </form>
          </Card>
        </div>
      </div>

      {list.map((item) => (
        <div key={item.id}>
          <p>{item.text}</p>
          <p>
            <small>Assigned to: {item.assignee}</small>
          </p>
          <p>
            <small>Difficulty: {item.difficulty}</small>
          </p>
          <div onClick={() => toggleComplete(item.id)}>
            Complete: {item.complete.toString()}
          </div>
          <hr />
        </div>
      ))}
    </>
  );
};

export default ToDo;
